    info = info.trim();

    if (info === '') {
        ctx.body = {};
        return;
    }

    switch (type) {
    case 'doi':
        await import_crossref(ctx, info);
        break;
    case 'pdf':
        await import_grobid(ctx, info);
        break;
    default:
        break;
