async function get_config(env) {
    const configs = await EntitiesUtils.search('config', {
        size: 1,
        where: {
            environment: ENV,
        },
    });

    let config = null;
    if ('result' in configs && 'hits' in configs.result && configs.result.hits.length > 0) {
        config = configs.result.hits[0].source;
    }

    return config;
