const mappings = require('./mappings');
// const mappings = require('/Users/Cocophotos/Desktop/mappings');
const settings = require('./settings');

const client = new elasticsearch.Client(config.elasticsearch);

function create() {
    /* Object.keys(mappings).forEach((name) => {
        let index = mappings[name];

        if (name in settings) {
            index = _.merge({}, mapping, settings[name]);
        } else {
            console.warn(`No settings for mapping ${name}`);
        }
        const response = client.indices.create({
            index: name, // `${config.elasticsearch.index_prefix}_${name}`,
            body: index,
        });

        response.then((result) => {
            console.log(`Success when creating index for ${name}`);
            console.log(result);
        }).catch((err) => {
            console.error(`Error when creating index for ${name}`);
            console.error(err);
        });
    });*/

    _.forEach(mappings, (mapping, name) => {
        let index = mapping;

        if (name in settings) {
            index = _.merge({}, mapping, settings[name]);
        } else {
            console.warn(`No settings for mapping ${name}`);
        }
        const response = client.indices.create({
            index: `${config.elasticsearch.index_prefix}_${name}`,
            body: index,
        });

        response.then((result) => {
            console.log(`Success when creating index for ${name}`);
            console.log(result);
        }).catch((err) => {
            console.error(`Error when creating index for ${name}`);
            console.error(err);
        });
    });
}

function update() {
    _.forEach(mappings, (mapping, name) => {
        const body = {
            properties: mapping.mappings[name].properties,
        };

        if ('_meta' in mapping.mappings[name]) {
            body._meta = mapping.mappings[name]._meta;
        }

        const response = client.indices.putMapping({
            index: `${config.elasticsearch.index_prefix}_${name}`,
            type: name,
            body,
        });

        response.then((result) => {
            console.log(`Success when updating mapping for index ${name}`);
            console.log(result);
        }).catch((err) => {
            console.error(`Error when updating mapping for index ${name}`);
            console.error(err);
        });
    });
}

module.exports = {
    create,
    update,
};

if (require.main === module) {
    const argv = require('funwithflags')(process.argv.slice(2));
    if ('update' in argv) {
        update();
    } else if ('create' in argv) {
        create();
    } else {
        console.error('Unknonw flags, you should only use --update or --create');
    }
}

const FS = require('fs');
const Path = require('path');
const Config = require('../config');
const Logger = require('../logger');

const resolver = p => Path.resolve(Path.join(Config.root, '../app/csl_styles', p));

const styles = {
    'ined-hceres': resolver('styles/hceres.csl'),
    'ined-apa': resolver('styles/apa.csl'),
    'ined-vancouver': resolver('styles/vancouver.csl'),
    'ined-harvard': resolver('styles/harvard.csl'),
    population: resolver('styles/population.csl'),
    chicago: resolver('styles/chicago.csl'),
    'iso-690': resolver('styles/iso690.csl'),
    'springer-humanities': resolver('styles/springer_humanities.csl'),
};


async function add_styles(Cite, mystyles) {
    for (const key in mystyles) {
        const filename = mystyles[key];
        try {
            const template = await new Promise((resolve, reject) => {
                FS.readFile(filename, 'utf-8', (err, data) => {
                    if (err) {
                        return reject(err);
                    }
                    return resolve(data);
                });
            });
            Cite.CSL.register.addTemplate(key, template);
        } catch (err) {
            Logger.error(`Error when reading ${filename}`);
            Logger.error(err);
        }
    }
    return Cite;
}

module.exports = {
    add_styles,
    styles,
};

const _ = require('lodash');
const Router = require('koa-router');
const Send = require('koa-send');
const Compose = require('koa-compose');
const Config = require('../config');
const RouterUtils = require('../modules/utils/router');
const BackRoutes = require('../../front/backoffice/routes');
const CommonRoutes = require('../../front/common/routes');
const EntitiesUtils = require('../modules/utils/entities');
const UploadUtils = require('../modules/utils/uploads');
const AuthUtils = require('../modules/utils/auth');
const UserRoutes = require('../modules/entities/user/routes');
const EntityRoutes = require('../modules/entities/entity/routes');
const ImporterRoutes = require('../modules/entities/importer/routes');
const ExporterRoutes = require('../modules/entities/exporter/routes');
const PublicationRoutes = require('../modules/entities/publication/routes');
const RssRoutes = require('../modules/3rdparty/rss/routes');

async function initialize_routes() {
    const router = new Router();

    const send_opts = {
        root: Config.root,
    };

    if (Config._env === 'production') {
        send_opts.maxage = 1000 * 60 * 60 * 24 * 7; // 7 days;
    } else {
        send_opts.maxage = 0;
    }

    CommonRoutes.forEach((route) => {
        router.get(route, async (ctx) => {
            await ctx.render('front/views/front');
        });
    });

    _.each(BackRoutes, (route) => {
        router.get(route, async (ctx) => {
            await ctx.render('back/views/back');
        });
    });

    router.get('/public/*', async (ctx) => {
        await Send(ctx, ctx.path, send_opts);
    });


    const response = await EntitiesUtils.search('entity', { size: 10000 });
    const extra_entities = response.result.hits.map(e => e.db.source.type);
    const entities = ['user', 'role', 'config', 'lang', 'form', 'function',
        'pipeline', 'widget', 'page', 'template', 'menu', 'query',
        'importer', 'exporter', 'connector', 'identifier', 'chart', 'mail_template', ...extra_entities];

    entities.forEach((e) => {
        RouterUtils.generate_entity_routes(router, e, []);
    });

    EntityRoutes(router);
    UserRoutes(router);
    ImporterRoutes(router);
    ExporterRoutes(router);
    RssRoutes(router);
    PublicationRoutes(router);

    const puprefix = `${Config.api.public.prefix}/${Config.api.public.version}`;
    router.post(`${puprefix}/single_upload`, Compose([...RouterUtils.upload_middlewares('upload',
        `${Config.root}/public/uploads`), UploadUtils.add_single]));
    router.get('/download/:entity/:eid/:filename', Compose([UploadUtils.download]));
    router.get('/downloads/:entity/:eid/:names/:filenames', Compose([UploadUtils.multi_download]));
    return router;
}

module.exports = initialize_routes;

