    });

    let config = null;
    if ('result' in configs && 'hits' in configs.result && configs.result.hits.length > 0) {
        config = configs.result.hits[0].source;
    }

    return config;
}

function retrieve_single_quantity(values) {
    if (values.length === 0) {
        return '';
    } else if (values.length === 1) {
        return values[0].value;
    }
    const results = values.filter(v => (v.quantity === '1' ||
            v.quantity === 'n/a'));
    return results.length === 0 ? '' : results[0].value;
}

async function get_language_values(key, config) {
    const values = await EntitiesUtils.search('lang', {
        size: config.langs.length,
        where: {
            $and: [
                { lang: config.langs.map(l => l.value) },
                { key },
            ],
        },
    });

    const hits = EntitiesUtils.get_hits(values);
    return hits.map((h) => {
        const src = h.source;
        const value = retrieve_single_quantity(src.values);
        return { lang: src.lang, value, key };
    });
}

function get_language_values_from_langs(key, langs) {
    return get_language_values(key, { langs });
}

async function get_language_values_from_langs_and_keys(keys, langs) {
    const values = await EntitiesUtils.search_and_get_sources('lang', {
        size: keys.length * langs.length,
        where: {
            $and: [
                { lang: langs },
                { key: keys },
            ],
        },
    });

    const items = values.reduce((obj, src) => {
        obj[src.lang][src.key] = retrieve_single_quantity(src.values);
        return obj;
    }, langs.reduce((o, l) => { o[l] = {}; return o; }, {}));
    return items;
}

module.exports = {
    get_config,
    get_language_values,
    get_language_values_from_langs,
    get_language_values_from_langs_and_keys,
};

const _ = require('lodash');
const Nodemailer = require('nodemailer');
const EntitiesUtils = require('./entities');

const env = process.env.NODE_ENV || 'development';

async function get_smtp_transport() {
    const configs = await EntitiesUtils.search_and_get_sources('config', {
        where: {
            environment: env,
        },
        size: 1,
    });

    if (configs.length === 0) {
        return null;
    }

    const config = configs[0];

    if (!('mail' in config) || !('smtp' in config.mail)) {
        return null;
    }

    const smtp = config.mail.smtp;

    if (!smtp.host || !smtp.port || !smtp.auth || !smtp.auth.user) {
        return null;
    }

    const transporter = Nodemailer.createTransport({
        host: smtp.host,
        port: parseInt(smtp.port, 10),
        secure: smtp.secure || false,
        auth: {
            user: smtp.auth.user,
            pass: smtp.auth.pass,
        },
    });
    return transporter;
}

async function send_email_through_smtp(options) {
    const transporter = await get_smtp_transport();
    if (transporter == null) {
        return null;
    }
    const info = await transporter.sendMail(options);
    return info;
}

function send_email(options, transport = 'smtp') {
    switch (transport) {
    default:
    case 'smtp': {
        return send_email_through_smtp(options);
    }
    }
}

function send_email_with(from, to, subject, msg, options = {}, transport = 'smtp') {
    const all_options = _.merge({}, options, {
        from, to, subject, text: msg, html: msg,
    });
    return send_email(all_options, transport);
}

module.exports = {
    send_email,
    send_email_with,
    get_smtp_transport,
};

